mapper.update:
UPDATE myplant SET myplant_nick = #{myplantNick},
                   myplant_pot = #{myplantPot},
                   myplant_length = #{myplantLength},
                   myplant_radius1 = #{myplantRadius1},
                   myplant_radius2 = #{myplantRadius2}
WHERE myplant_id = #{myplantId}
